SELECT 'Products' as type, COUNT(*) as count FROM products
UNION ALL SELECT 'Categories', COUNT(*) FROM product_categories
UNION ALL SELECT 'Stock Balances', COUNT(*) FROM stock_balances
UNION ALL SELECT 'FIFO Layers', COUNT(*) FROM fifo_layers;

-- ‡∏™‡∏£‡∏∏‡∏õ‡∏ï‡∏≤‡∏° Category
SELECT pc.name as category, COUNT(p.id) as products,
       COALESCE(SUM(sb.qty_on_hand * sb.avg_cost), 0)::numeric(15,2) as stock_value
FROM product_categories pc
LEFT JOIN products p ON p.category_id = pc.id
LEFT JOIN stock_balances sb ON sb.product_id = p.id
GROUP BY pc.id, pc.name ORDER BY stock_value DESC;